				property: "twitter:image",
				content: "https://ubuntucentar.com/assets/static/karijera-1.42db587.b514f32d297cc5afd76ee3459fb50fbe.jpg",
			},
		],
	},
	data() {
		return {
			success: false,
			formData: {
				name: "",
				last: "",
				email: "",
				submitStatus: null,
				phone: "",
				message: "",
				subject: "",
				city: "Online",
				skola: "",
				edukacija: "",
				sati: "",
			},
		};
	},

	validations: {
		formData: {
			name: {
				required,
			},
			last: {
				required,
			},
			email: {
				required,
				email,
			},
			message: {
				required,
			},
			subject: {
				required,
			},
		},
	},

	methods: {
		onClick() {
			let element = document.getElementById("scroll");
			element.scrollIntoView({ behavior: "smooth", block: "start" });
		},
		processFile() {
			this.formData.files = event.target.files;
		},

		encode(data) {
			const formData = new FormData();
			for (const key of Object.keys(data)) {
				if (key === "files") {
					formData.append(key, data[key][0]);
				} else {
					formData.append(key, data[key]);
				}
			}
			return formData;
		},

		submit(e) {
			this.$v.$touch();
			if (this.$v.formData.$invalid) {
				this.formData.submitStatus = "ERROR";
			} else {
				fetch("/", {
					method: "POST",
					body: this.encode({
						"form-name": e.target.getAttribute("name"),
						...this.formData,
					}),
				})
					.then(() => (this.formData.submitStatus = "OK"))
					.catch((error) => alert(error));
			}
		},
	},
	components: {
		CardItemKarijera,
	},
};
</script>
