			},
		],
	},

	data() {
		//povratni novi objekti

		return {
			formData: {
				name: "",
				last: "",
				email: "",
				submitStatus: null,
				phone: "",
				message: "",
				subject: "",
				city: "Novi Sad",
			},
		};
	},
	// validacija formi

	validations: {
		formData: {
			name: {
				required,
			},
			last: {
				required,
			},
			email: {
				required,
				email,
			},
			message: {
				required,
			},
			subject: {
				required,
			},
		},
	},
	methods: {
		encode(data) {
			//javascript objekat se pretvara u string radi dalje obrade

			return Object.keys(data)
				.map((key) => encodeURIComponent(key) + "=" + encodeURIComponent(data[key]))
				.join("&");
		},
		submit(e) {
			console.log("submit!");
			this.$v.$touch();
			// neispravno popunjena forma dobijanje greske

			if (this.$v.formData.$invalid) {
				this.formData.submitStatus = "ERROR";
			} else {

				// skupljeni podaci se salju  POST metodom
				fetch("/", {
					method: "POST",
					headers: {
						"Content-Type": "application/x-www-form-urlencoded",
					},
					body: this.encode({
						"form-name": e.target.getAttribute("name"),
						...this.formData,
					}),
				})
					// dobijanje odgovora sa servera

					.then(() => (this.formData.submitStatus = "OK"))
					.catch((error) => alert(error));
			}
		},
		//funkcija click za scrollovanje do elementa

		onClickTwo() {
			let element = document.getElementById("scroll");
			element.scrollIntoView({
				behavior: "smooth",
				block: "start",
			});
		},
	},
};
